        require(beforePool == afterPool, "fields-test-failed");
        require(
            beforeRewardToken.length == afterRewardToken.length &&
                beforeRewardToken[0] == afterRewardToken[0] &&
                beforeRewardPerToken[0] == afterRewardPerToken[0],
            "methods-test-failed"
        );
    }
}

